package com.mycompany.tutorial.ch03;

public class DenyLogicOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean play = true;
		
		play = !play;
		
		System.out.println(play);
		
		play = !play;
		
		System.out.println(play);
		
		System.out.println(!play);
		
		
	}

}
